function parse_paren(paren_char)
    paren_char=='(' ? ret_val=1 : ret_val=-1
    return ret_val
end

function parse_input(input_str)
    answer = 0;
    for a in input_str
        # println(a)
        to_add = parse_paren(a)
        # println(to_add)
        answer += to_add
    end
    return answer
end

function parse_input_part_two(input_str)
    answer = 0;
    it = 0;
    for a in input_str
        # println(a)
        to_add = parse_paren(a)
        # println(to_add)
        answer += to_add
        it += 1;
        if answer < 0
            return it
        end
    end
    return it
end

